`;

const MenuSection = styled.div`
  margin-bottom: 2rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid rgba(44, 62, 46, 0.1);
`;

const MenuItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  margin-bottom: 1rem;

  h3 {
    font-family: 'Sporting Grotesque', sans-serif;
