  }
`;

const LocalSourceNote = styled.div`
  font-size: 0.8rem;
  text-align: center;
  margin-top: 2rem;
  color: rgba(44, 62, 46, 0.6);
  font-style: italic;
`;

const FarmToTableMenu = () => {
  const menuSections = [
    {
      title: "Seasonal Starters",
      items: [
        {
          name: "Heirloom Tomato Composition",
          description: "Local tomatoes, basil from our greenhouse, aged balsamic",
          price: "$16"
        },
        {
          name: "Wild Foraged Mushroom Tartare",
          description: "Locally gathered mushrooms, quail egg, herb emulsion",
          price: "$18"
        }
      ]
    },
    {
      title: "Field & Pasture",
      items: [
        {
          name: "Heritage Pork Belly",
          description: "Berkshire pork, fermented honey, seasonal root vegetables",
          price: "$34"
        },
        {
          name: "Pasture-Raised Chicken",
          description: "Wood-fired, lavender herb rub, local grain risotto",
          price: "$32"
        }
      ]
    }
  ];

  return (
    <MenuContainer>
      <MenuFont>
        <h1 style={{
          fontFamily: "'Sporting Grotesque', sans-serif",
