}

.container {
    max-width: 600px;
    margin: 0 auto;
    padding: 2rem;
    text-align: center;
}

.logo {
    font-family: 'Instrument Serif', serif;
    font-size: 3.5rem;
    color: var(--accent-color);
    margin-bottom: 0.5rem;
    letter-spacing: -1px;
}

.tagline {
    font-style: italic;
    color: #666;
    margin-bottom: 2rem;
}

h2 {
    font-family: 'Instrument Serif', serif;
    border-bottom: 2px solid var(--accent-color);
    padding-bottom: 0.5rem;
    margin-bottom: 1rem;
}

.hours-grid, .contact-details {
    display: grid;
    gap: 0.5rem;
    background-color: white;
    padding: 1.5rem;
    border-radius: 8px;
    box-shadow: 0 4px 6px var(--soft-shadow);
}

.hours-grid {
    grid-template-columns: 1fr 1fr;
    text-align: left;
}

footer {
    margin-top: 2rem;
    font-size: 0.8rem;
    color: #777;
}
