```jsx
import React from 'react';

const products = [
  {
    id: 1,
    name: 'Asymmetric Deconstructed Jacket',
    price: '$1,250',
    image: '/jacket-1.jpg',
    description: 'Architectural silhouette. Fragmented construction.'
  },
  {
    id: 2,
    name: 'Liquid Metal Pants',
    price: '$890',
    image: '/pants-1.jpg',
    description: 'Mercurial form. Fluid movement.'
  },
  {
    id: 3,
    name: 'Digital Glitch Shirt',
    price: '$450',
    image: '/shirt-1.jpg',
    description: 'Coded imperfection. Algorithmic design.'
  }
];

function FeaturedProducts() {
  return (
    <section className="py-24 bg-neutral-900">
      <div className="container mx-auto px-8">
        <h2 className="text-6xl text-center mb-16 uppercase tracking-tighter">
          Featured Disruptions
        </h2>

        <div className="grid grid-cols-3 gap-8">
          {products.map(product => (
            <div 
              key={product.id} 
              className="group relative overflow-hidden transform transition-transform hover:scale-105"
            >
              <img 
                src={product.image} 
                alt={product.name}
                className="w-full h-[600px] object-cover grayscale group-hover:grayscale-0 transition-all duration-500"
              />
              <div className="absolute bottom-0 left-0 right-0 p-6 bg-black bg-opacity-70">
                <h3 className="text-2xl font-bold">{product.name}</h3>
                <p className="text-neutral-300">{product.description}</p>
                <p className="text-xl mt-2">{product.price}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default FeaturedProducts;
```

